		ie_status,
		dt_geracao,
		dt_liberacao,
		dt_retorno,
		nr_seq_informacao)
	values (	nr_seq_log_integracao_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		'P',
		clock_timestamp(),
		null,
		null,
		nr_seq_informacao_integracao_w);

	CALL wheb_exportar_hl7_pck.exportar(nr_seq_mensagem_hl7_w, nr_seq_log_integracao_w, ds_parametros_p);
end if;

