	v_query := 'CALL supplypoint_susp_prescr_atx ( ' || quote_nullable(nr_prescricao_p) || ',' || quote_nullable(nr_seq_material_p) || ',' || quote_nullable(nr_seq_horario_p) || ',' || quote_nullable(nm_usuario_p) || ',' || quote_nullable(nr_opcao_p) || ',' || quote_nullable(nr_seq_lote_p) || ' )';
