CREATE OR REPLACE PROCEDURE supplypoint_susp_prescr_atx ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint, nm_usuario_p text, nr_opcao_p bigint, nr_seq_lote_p bigint default null) AS $body$
DECLARE


/*
nr_opcao_p
1 - Suspender prescricaoo completa
2 - Suspender item prescricaoo
3 - Suspender horario prescricaoo
*/
cd_setor_atendimento_w		setor_atendimento.cd_setor_atendimento%type;
nr_seq_empresa_w		empresa_integracao.nr_sequencia%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
nr_seq_estrut_int_w		mat_estrutura.nr_sequencia%type;
ie_estrutura_w			varchar(1) := 'N';
ie_setor_swisslog_w		varchar(1) := 'N';
ds_param_integ_hl7_w		varchar(4000);
ie_urgente_w				varchar(1);
ie_enviar_horarios_disp_w	varchar(1);
BEGIN
begin
select	cd_setor_atendimento,
	cd_estabelecimento
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	'S'
into STRICT	ie_setor_swisslog_w
from	far_setores_integracao
where	nr_seq_empresa_int = 82
and	cd_setor_atendimento = cd_setor_atendimento_w;
exception
when others then
	null;
end;

select	coalesce(max('S'),'N')
into STRICT	ie_urgente_w
from	ap_lote a,
	classif_lote_disp_far b
where	a.nr_sequencia = nr_seq_lote_p
and	b.nr_sequencia = a.nr_seq_classif
and	b.ie_classif_urgente = 'A';

select coalesce(max(ie_enviar_horarios_disp), 'N')
into STRICT ie_enviar_horarios_disp_w
from parametros_farmacia
where cd_estabelecimento = cd_estabelecimento_w;

-- Suspender item prescricaoo completa
if (ie_setor_swisslog_w = 'S') and (nr_opcao_p = 2) and (ie_urgente_w = 'S' OR ie_enviar_horarios_disp_w = 'S') then


	ds_param_integ_hl7_w := 'NR_PRESCRICAO=' || nr_prescricao_p || obter_separador_bv
						 || 'NR_SEQ_MATERIAL=' || nr_seq_material_p || obter_separador_bv
						 || 'IE_ENVIAR_HORARIOS_DISP=' || ie_enviar_horarios_disp_w || obter_separador_bv;
	
	CALL gravar_agend_integracao(436, ds_param_integ_hl7_w);
	
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
