CREATE OR REPLACE PROCEDURE sup_ajustar_saldo_lote_consig ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp) AS $body$
DECLARE


cd_material_w		integer;
cd_local_estoque_w	smallint;
qt_estoque_w		double precision;
qt_estoque_lote_w		double precision;
qt_diferenca_w		double precision;

nr_seq_lote_w		bigint;

qt_estoque_ww		double precision;
qt_estoque_lote_ww	double precision;
qt_ajuste_w		double precision;
cd_fornecedor_w		varchar(14);

ie_aumenta_diminui_w	varchar(1);

c01 CURSOR FOR
SELECT	a.cd_material,
	a.cd_local_estoque,
	a.qt_estoque,
	sum(b.qt_estoque) qt_estoque_lote,
	a.cd_fornecedor
from	fornecedor_mat_consignado a,
	fornecedor_mat_consig_lote b
where	a.cd_material = b.cd_material
and	a.cd_local_estoque = b.cd_local_estoque
and	a.cd_estabelecimento = b.cd_estabelecimento
and	a.dt_mesano_referencia = b.dt_mesano_referencia
and	a.cd_fornecedor = b.cd_fornecedor
and	not exists (	select	1
			from	material y,
				emprestimo_material x,
				emprestimo w
			where	y.cd_material = x.cd_material
			and	x.nr_emprestimo = w.nr_emprestimo
			and	y.cd_material_estoque = a.cd_material
			and	x.qt_material > 0
			and	w.cd_estabelecimento = a.cd_estabelecimento)
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.dt_mesano_referencia = dt_mesano_referencia_p
having	a.qt_estoque <> sum(b.qt_estoque)
group by a.cd_material,
	a.cd_local_estoque,
	a.qt_estoque,
	a.cd_fornecedor;

c02 CURSOR FOR
SELECT	nr_seq_lote,
	qt_estoque
from	fornecedor_mat_consig_lote
where	cd_estabelecimento = cd_estabelecimento_p
and	dt_mesano_referencia = dt_mesano_referencia_p
and	cd_local_estoque = cd_local_estoque_w
and	cd_material = cd_material_w
and	cd_fornecedor = cd_fornecedor_w
order by qt_estoque desc;

c03 CURSOR FOR
SELECT	a.cd_material,
	a.cd_local_estoque,
	a.qt_estoque,
	a.cd_fornecedor
from	fornecedor_mat_consignado a
where	a.qt_estoque <> 0
and	substr(obter_se_material_estoque_lote(a.cd_estabelecimento, a.cd_material),1,1) = 'S'
and	not exists (	select	1
			from	fornecedor_mat_consig_lote b
			where	a.cd_material = b.cd_material
			and	a.cd_local_estoque = b.cd_local_estoque
			and	a.cd_estabelecimento = b.cd_estabelecimento
			and	a.cd_fornecedor	= b.cd_fornecedor
			and	a.dt_mesano_referencia = b.dt_mesano_referencia)
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.dt_mesano_referencia = dt_mesano_referencia_p;


BEGIN
/*delete	saldo_estoque_lote a
where	cd_estabelecimento = cd_estabelecimento_p
and	dt_mesano_referencia = dt_mesano_referencia_p
and	substr(obter_se_material_estoque_lote(cd_estabelecimento, cd_material),1,1) = 'N';*/
/*delete	saldo_estoque_lote a
where	cd_estabelecimento = cd_estabelecimento_p
and	dt_mesano_referencia = dt_mesano_referencia_p
and	not exists (	select	1
			from	saldo_estoque b
			where	a.cd_estabelecimento = b.cd_estabelecimento
			and	a.dt_mesano_referencia = b.dt_mesano_referencia
			and	a.cd_local_estoque = b.cd_local_estoque
			and	a.cd_material = b.cd_material);*/
open c01;
loop
fetch c01 into
	cd_material_w,
	cd_local_estoque_w,
	qt_estoque_w,
	qt_estoque_lote_w,
	cd_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	qt_diferenca_w := (qt_estoque_w - qt_estoque_lote_w);

	if (qt_diferenca_w > 0) then
		ie_aumenta_diminui_w := 'A';
	else
		ie_aumenta_diminui_w := 'D';
	end if;

	qt_diferenca_w	:= abs(qt_diferenca_w);

	open c02;
	loop
	fetch c02 into
		nr_seq_lote_w,
		qt_estoque_lote_ww;
	exit when ((c02%notfound) or (qt_diferenca_w = 0));
		begin
		if (ie_aumenta_diminui_w = 'A') then
			begin
			qt_ajuste_w	:= qt_diferenca_w;
			qt_diferenca_w	:= 0;
			end;
		else
			begin
			if (qt_estoque_lote_ww >= qt_diferenca_w) then
				qt_ajuste_w := qt_diferenca_w;
			else
				qt_ajuste_w := qt_estoque_lote_ww;
			end if;

			qt_diferenca_w	:= (qt_diferenca_w - qt_ajuste_w);
			qt_ajuste_w	:= (qt_ajuste_w * -1);
			end;
		end if;

		qt_estoque_ww	:= (qt_estoque_lote_ww + qt_ajuste_w);

		update	fornecedor_mat_consig_lote
		set	qt_estoque = qt_estoque_ww
		where	cd_estabelecimento = cd_estabelecimento_p
		and	dt_mesano_referencia = dt_mesano_referencia_p
		and	cd_local_estoque = cd_local_estoque_w
		and	cd_material = cd_material_w
		and	cd_fornecedor = cd_fornecedor_w
		and	nr_seq_lote = nr_seq_lote_w;

		RAISE NOTICE 'U: M=% Q=% L=%', cd_material_w, qt_estoque_ww, nr_seq_lote_w;
		end;
	end loop;
	close c02;
	end;
end loop;
close c01;

open c03;
loop
fetch c03 into
	cd_material_w,
	cd_local_estoque_w,
	qt_estoque_w,
	cd_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c03 */
	begin
	select	coalesce(max(nr_seq_lote_fornec),0)
	into STRICT	nr_seq_lote_w
	from	movimento_estoque a
	where	cd_estabelecimento = cd_estabelecimento_p
	and	dt_mesano_referencia = dt_mesano_referencia_p
	and	cd_local_estoque = cd_local_estoque_w
	and	cd_fornecedor = cd_fornecedor_w
	and	cd_material_estoque = cd_material_w;

	if (nr_seq_lote_w = 0) then
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_lote_w
		from 	material_lote_fornec a
		where	a.cd_estabelecimento = cd_estabelecimento_p
		and	a.cd_material = cd_material_w
		and	a.cd_cgc_fornec = cd_fornecedor_w
		and	a.ie_situacao = 'A'
		and	((ie_validade_indeterminada = 'S') or
			(dt_validade = (	SELECT	max(dt_validade)
					from 	material_lote_fornec x
					where	x.cd_estabelecimento = a.cd_estabelecimento
					and	x.cd_material = a.cd_material
					and	x.cd_cgc_fornec = a.cd_cgc_fornec
					and	x.ie_situacao = a.ie_situacao)));
	end if;

	if (nr_seq_lote_w > 0) then
		insert into fornecedor_mat_consig_lote(
			cd_estabelecimento,
			dt_mesano_referencia,
			cd_local_estoque,
			cd_material,
			nr_seq_lote,
			qt_estoque,
			nm_usuario,
			dt_atualizacao,
			cd_fornecedor)
		values (cd_estabelecimento_p,
			dt_mesano_referencia_p,
			cd_local_estoque_w,
			cd_material_w,
			nr_seq_lote_w,
			qt_estoque_w,
			'Tasy',
			clock_timestamp(),
			cd_fornecedor_w);
		RAISE NOTICE 'I: M=% Q=% L=%', cd_material_w, qt_estoque_w, nr_seq_lote_w;
	end if;
	end;
end loop;
close c03;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
