SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_alterar_bloqueio_lote ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_bloqueio_w	varchar(1);


BEGIN

select	coalesce(max(ie_bloqueio), 'N')
into STRICT	ie_bloqueio_w
from	material_lote_fornec
where	nr_sequencia	= nr_sequencia_p;

if (ie_bloqueio_w = 'S') then
	update	material_lote_fornec
	set	ie_bloqueio		= 'N',
		nm_usuario_bloqueio	 = NULL,
		dt_bloqueio		 = NULL
	where	nr_sequencia		= nr_sequencia_p;
else
	update	material_lote_fornec
	set	ie_bloqueio		= 'S',
		nm_usuario_bloqueio	= nm_usuario_p,
		dt_bloqueio		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
