CREATE OR REPLACE PROCEDURE sup_alterar_status_etiquetagem ( nr_seq_processo_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


/*
S - Separação
IP - Início preparo.
FP - Fim preparo.
C - Conferência.
*/
BEGIN

if (ie_status_p = 'S') then
	update	material_lote_fornec_etiq
	set	dt_separacao = clock_timestamp(),
		nm_usuario_separacao = nm_usuario_p
	where	nr_sequencia = nr_seq_processo_p;
elsif (ie_status_p = 'IP') then
	update	material_lote_fornec_etiq
	set	dt_inicio_preparo = clock_timestamp(),
		nm_usuario_preparo = nm_usuario_p
	where	nr_sequencia = nr_seq_processo_p;
elsif (ie_status_p = 'FP') then
	update	material_lote_fornec_etiq
	set	dt_fim_preparo = clock_timestamp()
	where	nr_sequencia = nr_seq_processo_p;
elsif (ie_status_p = 'C') then
	update	material_lote_fornec_etiq
	set	dt_conferencia = clock_timestamp(),
		nm_usuario_conferencia = nm_usuario_p
	where	nr_sequencia = nr_seq_processo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
