CREATE OR REPLACE PROCEDURE sup_alterar_status_integracao ( nr_documento_p bigint, ie_tipo_documento_p text, nm_usuario_p text) AS $body$
BEGIN
if (ie_tipo_documento_p = 'M') then
	begin
	update	w_movimento_estoque
	set	ie_status_integracao = 1
	where  	nr_sequencia = nr_documento_p;
	end;

elsif (ie_tipo_documento_p = 'O') then
	begin
	update	w_ordem_compra
	set	ie_status_integracao = 1
	where nr_documento_externo = nr_documento_p;
	end;

elsif (ie_tipo_documento_p = 'R') then
	begin
	update sup_int_requisicao
	set ie_status_integracao = 1
	where nr_requisicao = nr_documento_p;
	end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
