CREATE OR REPLACE PROCEDURE sup_altera_prior_cot_forn ( qt_prioridade_p bigint, ds_justificativa_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w		varchar(255);
ds_observacao_original_w		varchar(255);


BEGIN

select	ds_observacao
into STRICT	ds_observacao_original_w
from	cot_compra_forn_item
where	nr_sequencia = nr_sequencia_p;

if (ds_observacao_original_w IS NOT NULL AND ds_observacao_original_w::text <> '') then
	ds_observacao_w	:= substr(ds_observacao_original_w || chr(13) || chr(10) || ds_justificativa_p,1,255);
else
	ds_observacao_w	:= substr(ds_justificativa_p,1,255);
end if;

update	cot_compra_forn_item
set	qt_prioridade = qt_prioridade_p,
	ds_observacao = ds_observacao_w,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
