CREATE OR REPLACE PROCEDURE sup_atualizar_estoque_medio ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_local_estoque_w				integer;
cd_material_w					integer;
dt_mes_ref_w					timestamp;
qt_estoque_medio_w				double precision;
qt_consumo_w					double precision;

c01 CURSOR FOR
SELECT	distinct
	cd_local_estoque,
	cd_material
from	saldo_estoque
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mes_ref_w;


BEGIN

dt_mes_ref_w	:= trunc(dt_mesano_referencia_p,'month');

open c01;
loop
fetch c01 into
	cd_local_estoque_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	sup_obter_consumo_local_mat(cd_estabelecimento_p, cd_local_estoque_w, cd_material_w, dt_mes_ref_w,'Q')
	into STRICT	qt_consumo_w
	;

	qt_estoque_medio_w := Obter_estoque_medio_mat(cd_estabelecimento_p, cd_local_estoque_w, cd_material_w, dt_mes_ref_w, qt_estoque_medio_w);

	update	saldo_estoque
	set	qt_estoque_medio 	= qt_estoque_medio_w,
		qt_consumo		= qt_consumo_w
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	dt_mesano_referencia	= dt_mes_ref_w
	and	cd_local_estoque	= cd_local_estoque_w
	and	cd_material		= cd_material_w;
end loop;
close c01;

commit;
END;
$body$
