CREATE OR REPLACE PROCEDURE sup_atualizar_saldo_disp_req ( nr_requisicao_p bigint, cd_local_estoque_p bigint, cd_local_estoque_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_local_estoque_w	smallint;
cd_local_estoque_destino_w	smallint;
cd_estabelecimento_w	smallint;
dt_mesano_referencia_w	timestamp	:= trunc(clock_timestamp(),'mm');
dt_atualizacao_w		timestamp	:= clock_timestamp();
cd_cgc_fornecedor_w	varchar(14);
ie_consignado_w		varchar(1);
cd_material_w		integer;
nr_sequencia_w		integer;

qt_saldo_estoque_w	item_requisicao_material.qt_saldo_estoque%type;
qt_saldo_estoque_dest_w	item_requisicao_material.qt_saldo_estoque_dest%type;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_material,
	cd_cgc_fornecedor
from	item_requisicao_material
where	nr_requisicao = nr_requisicao_p;



BEGIN
cd_local_estoque_w 	:= cd_local_estoque_p;
cd_local_estoque_destino_w	:= cd_local_estoque_destino_p;

select	a.cd_estabelecimento,
	CASE WHEN coalesce(b.ie_consignado,'0')='0' THEN 'N'  ELSE 'S' END
into STRICT	cd_estabelecimento_w,
	ie_consignado_w
from	requisicao_material a,
	operacao_estoque b
where	a.cd_operacao_estoque = b.cd_operacao_estoque
and	a.nr_requisicao = nr_requisicao_p;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	cd_material_w,
	cd_cgc_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	qt_saldo_estoque_w	:= null;
	qt_saldo_estoque_dest_w	:= null;
	if (ie_consignado_w = 'S') then
		begin
		qt_saldo_estoque_w := obter_saldo_estoque_consig(cd_estabelecimento_w, cd_cgc_fornecedor_w, cd_material_w, cd_local_estoque_w);
		if (cd_local_estoque_destino_w IS NOT NULL AND cd_local_estoque_destino_w::text <> '') then
			qt_saldo_estoque_dest_w := obter_saldo_estoque_consig(cd_estabelecimento_w, cd_cgc_fornecedor_w, cd_material_w, cd_local_estoque_destino_w);
		end if;
		end;
	else
		begin
		qt_saldo_estoque_w := obter_saldo_disp_estoque(cd_estabelecimento_w, cd_material_w, cd_local_estoque_w, dt_mesano_referencia_w);
		if (cd_local_estoque_destino_w IS NOT NULL AND cd_local_estoque_destino_w::text <> '') then
			qt_saldo_estoque_dest_w := obter_saldo_disp_estoque(cd_estabelecimento_w, cd_material_w, cd_local_estoque_destino_w, dt_mesano_referencia_w);
		end if;
		end;
	end if;

	update	item_requisicao_material
	set	qt_saldo_estoque = qt_saldo_estoque_w,
		qt_saldo_estoque_dest = qt_saldo_estoque_dest_w,
		dt_atualizacao = dt_atualizacao_w,
		nm_usuario = nm_usuario_p
	where	nr_requisicao = nr_requisicao_p
	and	nr_sequencia = nr_sequencia_w;
	end;
end loop;
close c01;

commit;
end;
$body$
