CREATE OR REPLACE PROCEDURE sup_atualizar_saldo_invent ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_local_estoque_p bigint, cd_material_p bigint, qt_ocor_invent_p bigint, qt_ocor_invent_ok_p bigint, qt_inventario_p bigint, nm_usuario_p text, cd_fornecedor_p text default null) AS $body$
DECLARE


dt_mes_ref_w					timestamp;

/* qt_ocor_invent		Quantidade de Inventarios feitos
   qt_ocor_invent_ok	Quantidade de Inventários aprovados que não alteraram o estoque
   Qt_inventario		Quantidade inventariada do material no mês e no local
*/
BEGIN

dt_mes_ref_w	:= pkg_date_utils.start_of(dt_mesano_referencia_p, 'MONTH', 0);

update	saldo_estoque
set	qt_ocor_invent	= coalesce(qt_ocor_invent,0) + qt_ocor_invent_p,
	qt_ocor_invent_ok	= coalesce(qt_ocor_invent_ok,0) + qt_ocor_invent_ok_p,
	qt_inventario	= coalesce(qt_inventario,0) + abs(qt_inventario_p)
where	cd_estabelecimento		= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mes_ref_w
and	cd_local_estoque		= cd_local_estoque_p
and	cd_material		= cd_material_p;

update	fornecedor_mat_consignado
set	qt_ocor_invent		= coalesce(qt_ocor_invent,0) + qt_ocor_invent_p,
	qt_ocor_invent_ok		= coalesce(qt_ocor_invent_ok,0) + qt_ocor_invent_ok_p,
	qt_inventario		= coalesce(qt_inventario,0) + abs(qt_inventario_p)
where	cd_estabelecimento		= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mes_ref_w
and	cd_local_estoque		= cd_local_estoque_p
and	cd_material		= cd_material_p
and	cd_fornecedor		= cd_fornecedor_p;

END;
$body$
