CREATE OR REPLACE PROCEDURE sup_atualiza_qt_inspecao ( nr_seq_inspecao_p bigint) AS $body$
DECLARE


qt_lote_w			inspecao_recebimento_lote.qt_material%type;
qt_convertida_w			inspecao_recebimento_lote.qt_material%type;
cd_unidade_medida_compra_w	unidade_medida.cd_unidade_medida%type;
cd_unidade_medida_estoque_w	unidade_medida.cd_unidade_medida%type;
cd_material_w			material.cd_material%type;


BEGIN

select	b.cd_unidade_medida_compra,
	b.cd_unidade_medida_estoque,
	a.cd_material
into STRICT	cd_unidade_medida_compra_w,
	cd_unidade_medida_estoque_w,
	cd_material_w
from	inspecao_recebimento a,
	material b
where	a.cd_material = b.cd_material
and	a.nr_sequencia = nr_seq_inspecao_p;

select	coalesce(sum(qt_material),0)
into STRICT	qt_lote_w
from	inspecao_recebimento_lote
where	nr_seq_inspecao = nr_seq_inspecao_p;

select	obter_quantidade_convertida(cd_material_w, qt_lote_w, cd_unidade_medida_compra_w, cd_unidade_medida_estoque_w, 'S')
into STRICT	qt_convertida_w
;


update	inspecao_recebimento
set	qt_inspecao = qt_convertida_w
where	nr_sequencia = nr_seq_inspecao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
