CREATE OR REPLACE PROCEDURE sup_atualiza_status_item_lista ( nr_sequencia_p bigint, nr_seq_item_p bigint, nr_seq_status_p bigint, ie_consistencia_p text, ie_atualiza_todos_p text, cd_pessoa_resp_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w			bigint;
cd_pessoa_usuario_w		varchar(10);
nr_seq_parecer_w			bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	segmento_compras_rel_it
where	nr_seq_seg_rel = nr_sequencia_p
and	coalesce(dt_liberacao::text, '') = ''
--and	ds_observacao_p is not null
and	((ie_atualiza_todos_p = 'N' AND nr_sequencia = nr_seq_item_p) or (ie_atualiza_todos_p = 'S'));


BEGIN

if (ie_atualiza_todos_p = 'S') then
	begin

	update	segmento_compras_rel_it
	set	nr_seq_status_item = nr_seq_status_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		cd_pessoa_resp_lc = cd_pessoa_resp_p
	where	nr_seq_seg_rel = nr_sequencia_p
	and	coalesce(dt_liberacao::text, '') = '';

	end;
else
	begin

	update	segmento_compras_rel_it
	set	nr_seq_status_item = nr_seq_status_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		cd_pessoa_resp_lc = cd_pessoa_resp_p
	where	nr_sequencia = nr_seq_item_p;

	end;
end if;

select	cd_pessoa_fisica
into STRICT	cd_pessoa_usuario_w
from	usuario
where	nm_usuario_pesquisa = upper(nm_usuario_p);

open c01;
loop
fetch c01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('segmento_compras_rel_parec_seq')
	into STRICT	nr_seq_parecer_w
	;

	insert into segmento_compras_rel_parec(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			nr_seq_item_rel,
			dt_liberacao,
			dt_liberacao_parec,
			nm_usuario_lib_parec,
			ds_parecer) values (
				nr_seq_parecer_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_usuario_w,
				nr_seq_item_w,
				clock_timestamp(),
				null,
				nm_usuario_p,
				ds_observacao_p);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
