CREATE OR REPLACE PROCEDURE sup_cancela_email_pendente ( ie_tipo_mensagem_p bigint, nr_documento_p bigint, ie_tipo_documento_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	envio_email_compra_agrup a
where	cd_estabelecimento = cd_estabelecimento_p
and	ie_tipo_mensagem = coalesce(ie_tipo_mensagem_p,ie_tipo_mensagem)
and	nr_documento = nr_documento_p
and	ie_tipo_documento = ie_tipo_documento_p
and	coalesce(a.ie_cancelado,'N') = 'N'
and	exists (select	1
		from	email_compra_agrup_dest x
		where	x.nr_seq_envio = a.nr_sequencia
		and	coalesce(x.ie_enviado,'N') = 'N');


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	envio_email_compra_agrup
	set	dt_cancelamento = clock_timestamp(),
		ie_cancelado = 'S',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
