CREATE OR REPLACE PROCEDURE sup_carga_ajuste_custo_medio ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_centro_custo_p bigint, nm_usuario_p text, nr_sequencia_ajuste_p INOUT bigint) AS $body$
DECLARE


cd_material_w		integer;
vl_custo_medio_w		double precision;
nr_sequencia_ajuste_w	bigint;
dt_atualizacao_w		timestamp := clock_timestamp();
ie_gerou_w		varchar(1) := 'N';
vl_custo_medio_atual_w	double precision;
qt_gerado_w		bigint;
dt_mesano_referencia_w	timestamp;

C01 CURSOR FOR
	SELECT	b.cd_material_estoque,
		avg(a.vl_custo_medio)
	from	sup_impl_cm a,
		material b
	where	a.cd_material = b.cd_material
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and	coalesce(a.vl_custo_medio,0) > 0
	group by b.cd_material_estoque;


BEGIN
select	coalesce(max(dt_mesano_vigente),trunc(clock_timestamp(),'mm'))
into STRICT	dt_mesano_referencia_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p
and	ie_situacao = 'A';

select	nextval('sup_ajuste_estoque_consumo_seq')
into STRICT	nr_sequencia_ajuste_w
;

insert into sup_ajuste_estoque_consumo(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	dt_mesano_referencia,
	dt_registro,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_local_estoque,
	cd_centro_custo,
	ie_tipo_ajuste)
values (nr_sequencia_ajuste_w,
	cd_estabelecimento_p,
	dt_atualizacao_w,
	nm_usuario_p,
	trunc(dt_mesano_referencia_w,'mm'),
	dt_atualizacao_w,
	dt_atualizacao_w,
	nm_usuario_p,
	cd_local_estoque_p,
	cd_centro_custo_p,
	'CM');

open C01;
loop
fetch C01 into
	cd_material_w,
	vl_custo_medio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	vl_custo_medio_atual_w := coalesce(obter_custo_medio_material(cd_estabelecimento_p,dt_mesano_referencia_w,cd_material_w),0);

	insert into sup_ajuste_est_cons_mat(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_ajuste,
		cd_material,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		vl_ajuste,
		vl_custo_medio)
	values (nextval('sup_ajuste_est_cons_mat_seq'),
		dt_atualizacao_w,
		nm_usuario_p,
		nr_sequencia_ajuste_w,
		cd_material_w,
		dt_atualizacao_w,
		nm_usuario_p,
		vl_custo_medio_w,
		vl_custo_medio_atual_w);

	ie_gerou_w := 'S';

	qt_gerado_w := coalesce(qt_gerado_w,0) + 1;

	if (qt_gerado_w = 500) then
		begin
		qt_gerado_w := 0;
		commit;
		end;
	end if;
	end;
end loop;
close C01;

if (ie_gerou_w = 'S') then
	delete FROM sup_impl_cm where coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;
else
	begin
	delete FROM sup_ajuste_estoque_consumo where nr_sequencia = nr_sequencia_ajuste_w;
	nr_sequencia_ajuste_w := 0;
	end;
end if;

nr_sequencia_ajuste_p := nr_sequencia_ajuste_w;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
