CREATE OR REPLACE PROCEDURE sup_comunicar_cancel_ordem ( nm_usuario_destino_p text, ds_comunicado_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_classif_w	bigint;


BEGIN

select	obter_classif_comunic('F')
into STRICT	nr_seq_classif_w
;

if (nm_usuario_destino_p IS NOT NULL AND nm_usuario_destino_p::text <> '') then
	select	nextval('comunic_interna_seq')
	into STRICT	nr_sequencia_w
	;

	insert into comunic_interna(
			dt_comunicado,
			ds_titulo,
			ds_comunicado,
			nm_usuario,
			dt_atualizacao,
			ie_geral,
			nm_usuario_destino,
			nr_sequencia,
			ie_gerencial,
			nr_seq_classif,
			dt_liberacao)
		values (	clock_timestamp(),
			wheb_mensagem_pck.get_Texto(802142),
			ds_comunicado_p,
			nm_usuario_p,
			clock_timestamp(),
			'N',
			nm_usuario_destino_p,
			nr_sequencia_w,
			'N',
			nr_seq_classif_w,
			clock_timestamp());
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
