CREATE OR REPLACE PROCEDURE sup_consistir_alterar_contagem ( nr_seq_inventario_p bigint, ie_contagem_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_lotes_w		bigint;
qt_contagens_w		bigint;


BEGIN

if (ie_contagem_p = 2) then

	select	count(*)
	into STRICT	qt_contagens_w
	from 	inventario_material
	where 	coalesce(qt_contagem::text, '') = ''
	and 	coalesce(qt_inventario::text, '') = ''
	and 	nr_seq_inventario = nr_seq_inventario_p;

	if (qt_contagens_w > 0) then
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279150);
	end if;

	if (coalesce(ds_erro_p::text, '') = '') then
		select	count(*),
			count(qt_contagem)
		into STRICT	qt_lotes_w,
			qt_contagens_w
		from 	inventario_material_lote
		where 	coalesce(qt_inventario::text, '') = ''
		and 	nr_seq_inventario = nr_seq_inventario_p;

		if (qt_lotes_w > qt_contagens_w) then
			ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279151);
		end if;
	end if;

elsif (ie_contagem_p = 3) then

	select	count(*)
	into STRICT	qt_contagens_w
	from 	inventario_material
	where 	coalesce(qt_recontagem::text, '') = ''
	and 	coalesce(qt_inventario::text, '') = ''
	and 	nr_seq_inventario = nr_seq_inventario_p;

	if (qt_contagens_w > 0) then
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279152);
	end if;

	if (coalesce(ds_erro_p::text, '') = '') then
		select	count(*),
			count(qt_recontagem)
		into STRICT	qt_lotes_w,
			qt_contagens_w
		from 	inventario_material_lote
		where 	coalesce(qt_inventario::text, '') = ''
		and 	nr_seq_inventario = nr_seq_inventario_p;

		if (qt_lotes_w > qt_contagens_w) then
			ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279153);
		end if;
	end if;

elsif (ie_contagem_p = 4) then

	select	count(*)
	into STRICT	qt_contagens_w
	from 	inventario_material
	where 	coalesce(qt_seg_recontagem::text, '') = ''
	and 	coalesce(qt_inventario::text, '') = ''
	and 	nr_seq_inventario = nr_seq_inventario_p;

	if (qt_contagens_w > 0) then
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279154);
	end if;

	if (coalesce(ds_erro_p::text, '') = '') then
		select	count(*),
			count(qt_seg_recontagem)
		into STRICT	qt_lotes_w,
			qt_contagens_w
		from 	inventario_material_lote
		where 	coalesce(qt_inventario::text, '') = ''
		and 	nr_seq_inventario = nr_seq_inventario_p;

		if (qt_lotes_w > qt_contagens_w) then
			ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279153);
		end if;
	end if;

end if;

commit;

end;
$body$
