SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_criar_gerar_ajuste_cm_job (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


jobno			sup_ajuste_estoque_consumo.nm_id_job%type;
ds_comando_job_w	varchar(2000);

-- falhas is not null, significa que ja foi executado pelo menos uma vez, se ja foi executado ao menos uma vez, apaga.
-- Durante a execucao ele fica null
C01 CURSOR FOR
	SELECT	job
	from	job_v
	where	comando	like 'sup_gerar_ajuste_cm%'
	and	(falhas IS NOT NULL AND falhas::text <> '');

BEGIN
-- Verificar se a job ja existe, se existir remove
for r_C01_w in C01 loop
	dbms_job.remove(r_C01_w.job);
	commit;
end loop;

ds_comando_job_w := 'sup_gerar_ajuste_cm(' || nr_sequencia_p || ','
					|| pls_util_pck.aspas_w || nm_usuario_p || pls_util_pck.aspas_w ||  ');';

-- Vai iniciar a procedure em 2.5 segundos apos a conclusao desta rotina
dbms_job.submit(jobno, ds_comando_job_w, clock_timestamp() + interval '216000 seconds' * (1/24/60/60));

-- coloca o id do job responsavel por executar o processo.
update	sup_ajuste_estoque_consumo
set	nm_id_job	= jobno,
	nm_id_sid	 = NULL,
	nm_id_serial	 = NULL
where	nr_sequencia	= nr_sequencia_p;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sup_criar_gerar_ajuste_cm_job (nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

