CREATE OR REPLACE PROCEDURE sup_desativar_enviar_email ( nr_seq_comunicado_p bigint, nr_seq_local_p bigint ) AS $body$
BEGIN		
		 
update	suporte_comunicado_dest a      
set	a.ie_enviar_email = 'N' 
where	a.nr_seq_comunicado = nr_seq_comunicado_p 
and	a.cd_pessoa_fisica in (	SELECT	b.cd_pessoa_fisica 
				from	man_pessoa_localizacao b  
				where	b.nr_seq_localizacao = nr_seq_local_p	);
				 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
