SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_duplicar_registro_kit ( nr_sequencia_p bigint, qt_duplicar_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reg_w		bigint;
qt_duplicado_w		bigint;
dt_atualizacao_w	timestamp := clock_timestamp();


BEGIN

qt_duplicado_w := 0;

while(qt_duplicado_w < qt_duplicar_p) loop
	begin

	select	nextval('kit_estoque_reg_seq')
	into STRICT	nr_seq_reg_w
	;

	insert into kit_estoque_reg(
		nr_sequencia,
		nr_seq_reg_origem,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_resp,
		nm_usuario_lib,
		ds_registro_kit,
		ie_situacao,
		ie_basico,
		cd_local_estoque,
		ie_origem,
		ie_excluido)
	SELECT	nr_seq_reg_w,
		nr_sequencia_p,
		cd_estabelecimento,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_nrec,
		cd_pessoa_resp,
		null,
		ds_registro_kit,
		'A',
		ie_basico,
		cd_local_estoque,
		ie_origem,
		'N'
	from	kit_estoque_reg
	where	nr_sequencia = nr_sequencia_p;

	insert into kit_estoque(
		nr_sequencia,
		cd_kit_material,
		dt_atualizacao,
		nm_usuario,
		dt_montagem,
		nm_usuario_montagem,
		cd_estabelecimento,
		cd_medico,
		cd_local_estoque,
		cd_convenio,
		ie_tipo_convenio,
		ie_status,
		ie_sexo,
		ds_observacao,
		nr_seq_kit_origem,
		nr_seq_reg_kit)
	SELECT	nextval('kit_estoque_seq'),
		cd_kit_material,
		dt_atualizacao_w,
		nm_usuario_p,
		dt_atualizacao_w,
		nm_usuario_p,
		cd_estabelecimento,
		cd_medico,
		cd_local_estoque,
		cd_convenio,
		ie_tipo_convenio,
		ie_status,
		ie_sexo,
		ds_observacao,
		nr_sequencia,
		nr_seq_reg_w
	from	kit_estoque
	where	nr_seq_reg_kit = nr_sequencia_p;

	insert into kit_estoque_comp(
		nr_seq_kit_estoque,
		nr_sequencia,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		qt_material,
		ie_gerado_barras,
		nr_seq_lote_fornec,
		nr_seq_motivo_exclusao,
		nr_seq_item_kit,
		dt_exclusao,
		nm_usuario_exclusao,
		cd_material_troca,
		nm_usuario_troca,
		ds_motivo_troca,
		nr_seq_reg_kit)
	SELECT (SELECT nr_sequencia from kit_estoque where nr_seq_reg_kit = nr_seq_reg_w and nr_seq_kit_origem = a.nr_sequencia) nr_sequencia,
		row_number() OVER () AS rownum,
		b.cd_material,
		dt_atualizacao_w,
		nm_usuario_p,
		b.qt_material,
		b.ie_gerado_barras,
		b.nr_seq_lote_fornec,
		b.nr_seq_motivo_exclusao,
		b.nr_seq_item_kit,
		b.dt_exclusao,
		b.nm_usuario_exclusao,
		b.cd_material_troca,
		b.nm_usuario_troca,
		b.ds_motivo_troca,
		nr_seq_reg_w
	from	kit_estoque_comp b,
		kit_estoque a,
		kit_estoque_reg c
	where	a.nr_seq_reg_kit = nr_sequencia_p
	and	b.nr_seq_kit_estoque = a.nr_sequencia
	and	a.nr_seq_reg_kit = c.nr_sequencia
	and	b.ie_gerado_barras = 'S'
	and	coalesce(dt_exclusao::text, '') = ''
	order by nr_sequencia;

	qt_duplicado_w := (qt_duplicado_w + 1);
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sup_duplicar_registro_kit ( nr_sequencia_p bigint, qt_duplicar_p bigint, nm_usuario_p text) FROM PUBLIC;

