CREATE OR REPLACE PROCEDURE sup_enviar_mensagens_pend ( ie_tipo_mensagem_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text default null) AS $body$
DECLARE

 
ie_tipo_mensagem_w bigint;

c01 CURSOR FOR 
SELECT ie_tipo_mensagem 
from mensagens_pendentes_compras_v 
where cd_estabelecimento = cd_estabelecimento_p 
and (ie_tipo_mensagem = ie_tipo_mensagem_p or ie_tipo_mensagem_p = 0) 
group by ie_tipo_mensagem;


BEGIN 
 
open c01;
loop 
fetch c01 into 
 ie_tipo_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 begin 
 
 CALL envia_email_compra_agrupado(ie_tipo_mensagem_w, 'S', cd_estabelecimento_p,nm_usuario_p);
 
 end;
end loop;
close c01;
 
end;
$body$
