CREATE OR REPLACE PROCEDURE sup_estornar_lib_transf_ordem ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

select count(*)
into STRICT qt_existe_w
from nota_fiscal
where nr_ordem_compra = nr_ordem_compra_p
and ie_situacao = '1';

if (qt_existe_w <> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(198867);
end if;

update	ordem_compra
set	dt_liberacao  = NULL,
	dt_aprovacao  = NULL
where	nr_ordem_compra = nr_ordem_compra_p;

update	ordem_compra_item
set	dt_aprovacao  = NULL
where	nr_ordem_compra = nr_ordem_compra_p;

CALL gravar_historico_transf_quimio(nm_usuario_p,nr_ordem_compra_p,'E',WHEB_MENSAGEM_PCK.get_texto(306924));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
