SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_estornar_receb_solic_unit ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


--SOLIC_UNITARIZACAO
cd_local_origem_w	solic_unitarizacao.cd_local_estoque_origem%type;
cd_local_destino_w	solic_unitarizacao.cd_local_estoque_destino%type;

cd_operacao_estoque_w	smallint;


BEGIN

select	cd_local_estoque_origem,
	cd_local_estoque_destino
into STRICT	cd_local_origem_w,
	cd_local_destino_w
from	solic_unitarizacao
where	nr_sequencia = nr_sequencia_p;

if (cd_local_origem_w <> cd_local_destino_w) then
	cd_operacao_estoque_w := sup_obter_operacao_unit('ST');
	CALL gerar_movto_estoque_trans_unit(nr_sequencia_p, cd_operacao_estoque_w, '2', 'O', nm_usuario_p);
	cd_operacao_estoque_w := sup_obter_operacao_unit('ET');
	CALL gerar_movto_estoque_trans_unit(nr_sequencia_p, cd_operacao_estoque_w, '2', 'D', nm_usuario_p);
end if;

update	solic_unitarizacao
set	cd_pessoa_recebimento 	 = NULL,
	dt_recebimento 		 = NULL,
	cd_local_estoque_destino  = NULL,
	dt_atualizacao 		= clock_timestamp(),
	nm_usuario 		= nm_usuario_p,
	dt_estorno_receb	= clock_timestamp()
where	nr_sequencia 		= nr_sequencia_p;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
