CREATE OR REPLACE PROCEDURE sup_excluir_importacao_impl ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_local_estoque_p bigint, ie_tipo_requisicao_p text, ie_consignado_p text) AS $body$
BEGIN
if (cd_local_estoque_p = 0) then
	delete
	from	movimento_estoque
	where	nr_movimento_estoque in (
		SELECT	a.nr_movimento_estoque
		from	operacao_estoque b,
			movimento_estoque a
		where	a.cd_operacao_estoque	= b.cd_operacao_estoque
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	a.dt_mesano_referencia	= dt_mesano_referencia_p
		and	b.ie_tipo_requisicao		= ie_tipo_requisicao_p
		and	b.ie_consignado		= ie_consignado_p);
else
	delete
	from	movimento_estoque
	where	nr_movimento_estoque in (
		SELECT	a.nr_movimento_estoque
		from	operacao_estoque b,
			movimento_estoque a
		where	a.cd_operacao_estoque	= b.cd_operacao_estoque
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	a.cd_local_estoque		= cd_local_estoque_p
		and	b.ie_tipo_requisicao		= ie_tipo_requisicao_p
		and	b.ie_consignado		= ie_consignado_p);
end if;

commit;

CALL verifica_dif_saldo(dt_mesano_referencia_p,1555);
CALL verifica_dif_saldo_lote(dt_mesano_referencia_p,1555);
CALL ajusta_saldo_consig(dt_mesano_referencia_p,cd_estabelecimento_p,1555);
CALL verifica_dif_saldo_lote_consig(dt_mesano_referencia_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
