CREATE OR REPLACE PROCEDURE sup_excluir_saldo_projeto ( nr_seq_projeto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_ultima_sequencia_w	bigint;
dt_mesano_referencia_w	timestamp;
nr_seq_projeto_w		bigint;
vl_entrada_w                	double precision;
vl_saida_w                  	double precision;
vl_saldo_w                  	double precision;
vl_anterior_w               	double precision;
vl_saldo_anterior_w		double precision;


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_ultima_sequencia_w
from	projeto_recurso_saldo a
where	a.nr_seq_projeto = nr_seq_projeto_p;

if (nr_ultima_sequencia_w <> 0) then
	begin

	select	dt_mesano_referencia,
		nr_seq_projeto
	into STRICT	dt_mesano_referencia_w,
		nr_seq_projeto_w
	from	projeto_recurso_saldo
	where	nr_sequencia = nr_ultima_sequencia_w;

	delete
	from	projeto_rec_saldo_diario
	where	trunc(dt_saldo,'mm') = trunc(dt_mesano_referencia_w,'mm')
	and	nr_seq_projeto = nr_seq_projeto_w;

	delete
	from	projeto_rec_saldo_razao
	where	trunc(dt_referencia,'mm') = trunc(dt_mesano_referencia_w,'mm')
	and	nr_seq_projeto = nr_seq_projeto_w;

	delete	FROM projeto_recurso_saldo
	where	nr_sequencia = nr_ultima_sequencia_w;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
