SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_finalizar_proc_unit ( nr_sequencia_p bigint, nm_usuario_p text, ie_consiste_ativ_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_ativ_aberto_w	bigint;
qt_itens_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_itens_w
from	item_unitarizacao
where	nr_seq_unitarizacao = nr_sequencia_p;

if (qt_itens_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(227397);
end if;

if (ie_consiste_ativ_p = 'S') then

	select	count(*)
	into STRICT	qt_ativ_aberto_w
	from	unitarizacao_atividade
	where 	nr_seq_unitarizacao = nr_sequencia_p
	and (coalesce(dt_inicio_atividade::text, '') = '' or	coalesce(dt_fim_atividade::text, '') = '');

	if (qt_ativ_aberto_w > 0) then
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(278844);
	end if;

end if;

if (coalesce(ds_erro_p::text, '') = '') then
	update	unitarizacao
	set	dt_fim_processo		= clock_timestamp(),
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p
	where	nr_sequencia 		= nr_sequencia_p;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
