CREATE OR REPLACE PROCEDURE sup_gerar_ajuste_estoque_mes ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_observacao_w		varchar(255);
dt_mesano_referencia_w	timestamp;


BEGIN

select	dt_mesano_vigente
into STRICT	dt_mesano_referencia_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_p;

ds_observacao_w	:= 'De=' || dt_mesano_referencia_w || ' Para=' || PKG_DATE_UTILS.ADD_MONTH(dt_mesano_referencia_w, -1, 0);

update	parametro_estoque
set	dt_ultima_virada	= PKG_DATE_UTILS.ADD_MONTH(dt_ultima_virada, -1, 0),
	dt_mesano_vigente	= PKG_DATE_UTILS.ADD_MONTH(dt_mesano_vigente, -1, 0),
	dt_mesano_proximo	= PKG_DATE_UTILS.ADD_MONTH(dt_mesano_proximo, -1, 0)
where	cd_estabelecimento	= cd_estabelecimento_p;


insert into sup_log_ajuste_estoque(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ie_tipo,
	dt_mesano_referencia,
	ds_observacao)
values ( nextval('sup_log_ajuste_estoque_seq'),
	cd_estabelecimento_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	'MES',
	dt_mesano_referencia_w,
	ds_observacao_w);


commit;

end;
$body$
