CREATE OR REPLACE PROCEDURE sup_gerar_imp_marca ( nm_usuario_p text, cd_estabelecimento_p bigint, ds_tipo_marca_p text, ds_marca_p text, cd_material_p bigint, dt_validade_reg_anvisa_p timestamp, nr_registro_anvisa_p text, cd_referencia_p text default null) AS $body$
DECLARE


nr_seq_tipo_marca_w	bigint;
nr_seq_marca_w		bigint;
qt_material_marca_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_tipo_marca_w
from	tipo_marca
where	upper(ds_tipo_marca) = upper(ds_tipo_marca_p);

if (nr_seq_tipo_marca_w = 0) then

	select	nextval('tipo_marca_seq')
	into STRICT	nr_seq_tipo_marca_w
	;

	insert into tipo_marca(
		ds_tipo_marca,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_situacao,
		nm_usuario,
		nm_usuario_nrec,
		nr_sequencia)
	values (	ds_tipo_marca_p,
		clock_timestamp(),
		clock_timestamp(),
		'A',
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_tipo_marca_w);
end if;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_marca_w
from	marca
where	upper(ds_marca) = upper(ds_marca_p)
and	nr_seq_tipo = nr_seq_tipo_marca_w;

if (nr_seq_marca_w = 0) then
	select	nextval('marca_seq')
	into STRICT	nr_seq_marca_w
	;

	insert into marca(
		ds_marca,
		ds_observacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_situacao,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_tipo,
		nr_sequencia,
		qt_dia_ressup)
	values (	ds_marca_p,
		wheb_mensagem_pck.get_texto(309630),--'Marca cadastrada por importação.',
		clock_timestamp(),
		clock_timestamp(),
		'A',
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_tipo_marca_w,
		nr_seq_marca_w,
		7);
end if;

select	count(*)
into STRICT	qt_material_marca_w
from	material_marca
where	cd_material = cd_material_p
and	nr_sequencia = nr_seq_marca_w;

if (qt_material_marca_w = 0) then
	insert into material_marca(
		cd_estabelecimento,
		cd_material,
		ds_observacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_validade_reg_anvisa,
		ie_situacao,
		nm_usuario,
		nm_usuario_nrec,
		nr_registro_anvisa,
		nr_sequencia,
		cd_referencia)
	values (	cd_estabelecimento_p,
		cd_material_p,
		wheb_mensagem_pck.get_texto(309631),--'Material marca cadastrado por importação.',
		clock_timestamp(),
		clock_timestamp(),
		dt_validade_reg_anvisa_p,
		'A',
		nm_usuario_p,
		nm_usuario_p,
		nr_registro_anvisa_p,
		nr_seq_marca_w,
		cd_referencia_p);
else
	update	material_marca
	set	dt_validade_reg_anvisa = coalesce(dt_validade_reg_anvisa, dt_validade_reg_anvisa_p),
		nr_registro_anvisa = coalesce(nr_registro_anvisa, nr_registro_anvisa_p),
		cd_referencia = coalesce(cd_referencia, cd_referencia_p)
	where	nr_sequencia = nr_seq_marca_w
	and	cd_material = cd_material_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
