CREATE OR REPLACE PROCEDURE sup_gerar_inspecao_registro ( cd_cnpj_p text, cd_pessoa_fisica_p text, nr_seq_registro_p INOUT bigint, cd_estabelecimento_p bigint, ds_senha_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w         		bigint;
cd_pessoa_responsavel_w		varchar(10);


BEGIN 
 
select	nextval('inspecao_registro_seq') 
into STRICT	nr_sequencia_w
;
 
select	coalesce(max(substr(obter_dados_usuario_opcao(nm_usuario_p,'C'),1,10)),'X') 
into STRICT	cd_pessoa_responsavel_w
;
 
if (cd_pessoa_responsavel_w = 'X') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266284);
	--'Usuário deve estar vinculado com uma pessoa física.'); 
end if;
 
insert into inspecao_registro(nr_sequencia, 
			cd_estabelecimento, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			dt_registro, 
			cd_pessoa_resp, 
			cd_cnpj, 
			cd_pessoa_fisica, 
			ie_origem, 
			nr_senha) 
	values ( nr_sequencia_w, 
			cd_estabelecimento_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			cd_pessoa_responsavel_w, 
			coalesce(cd_cnpj_p,''), 
			coalesce(cd_pessoa_fisica_p,''), 
			'GF', 
			ds_senha_p);
 
nr_seq_registro_p := nr_sequencia_w;
 
commit;
 
end;
