CREATE OR REPLACE PROCEDURE sup_gerar_unitarizacao_item ( qtd_registro_p bigint, qtd_material_p bigint, nr_seq_unitarizacao_p bigint, nm_usuario_p text) AS $body$
DECLARE

	
nr_sequencia_w bigint;

BEGIN

if (coalesce(qtd_registro_p,0) <> 0) and (coalesce(qtd_material_p,0) <> 0) then
begin
	for i in 1..qtd_registro_p loop
	begin
		
		Select nextval('item_unitarizacao_seq')
		into STRICT	nr_sequencia_w
		;

		insert into ITEM_UNITARIZACAO(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_unitarizacao,
			ie_tipo_processo,
			qt_material,
			dt_atualizacao_nrec,
			nr_seq_motivo_perda,
			nm_usuario_nrec,
			nr_seq_atividade
		) values (
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_unitarizacao_p,
			'A',
			qtd_material_p,
			clock_timestamp(),
			null,
			nm_usuario_p,
			null);
			
		commit;
	end;
	end loop;
end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
