CREATE OR REPLACE PROCEDURE sup_gravar_barras_mat_emissao ( cd_material_p bigint, cd_barras_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	material_cod_barra
where	cd_barra_material = cd_barras_p;

if (qt_existe_w = 0) then
	begin

	insert into material_cod_barra(
		cd_material,
		cd_barra_material,
		dt_atualizacao,
		nm_usuario,
		cd_cgc_fabricante,
		ds_observacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_lote_fornec) values (
			cd_material_p,
			cd_barras_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			ds_observacao_p,
			clock_timestamp(),
			nm_usuario_p,
			null);

	commit;

	end;
end if;

end;
$body$
