CREATE OR REPLACE PROCEDURE sup_gravar_historico_material (cd_estab_p bigint, cd_material_p bigint, ds_titulo_p text, ds_historico_p text, ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w				text;
nr_sequencia_w				bigint;

/* Caso Exista Formatação Negrito */

qt_existe_Format_w				bigint;
qt_existe_w		bigint;


BEGIN

ds_historico_w	:= substr(ds_historico_p,1,4000);

select	position('{\ul{\b' in ds_historico_w)
into STRICT	qt_existe_Format_w
;

if (qt_existe_Format_w > 0) then
	ds_historico_w	:= substr('{\rtf1\ansi\deff0{\fonttbl ' ||
				'{\f0\fnil\fcharset0 Verdana;}{\f1\fnil Verdana;}}' ||
				'{\colortbl ;\red0\green0\blue128;}'||
				'\viewkind4\uc1\pard\cf1\lang1046\f0\fs20' ||
				ds_historico_w || '\b0 }',1,4000);
end if;

select	count(*)
into STRICT	qt_existe_w
from 	material
where 	cd_material = cd_material_p;

if (qt_existe_w > 0) then

	select	nextval('material_historico_seq')
	into STRICT	nr_sequencia_w
	;

	insert into material_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_historico,
		ds_historico_curto,
		cd_material,
		ds_titulo,
		cd_estabelecimento,
		ie_tipo)
	values (	nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_historico_w,
		substr(ds_historico_w,1,4000),
		cd_material_p,
		ds_titulo_p,
		CASE WHEN cd_estab_p=0 THEN null  ELSE cd_estab_p END ,
		ie_tipo_p);
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
