CREATE OR REPLACE PROCEDURE sup_inativa_item_nao_cotado ( nr_cot_compra_p bigint) AS $body$
DECLARE


nr_item_cot_compra_w		integer;

c01 CURSOR FOR
SELECT	a.nr_item_cot_compra
from	cot_compra_item a
where	a.nr_cot_compra = nr_cot_compra_p
and	coalesce(a.ie_situacao,'A') = 'A'
and not exists (
	SELECT	1
	from	cot_compra_forn_item b
	where	b.nr_cot_compra = nr_cot_compra_p
	and	a.nr_item_cot_compra = b.nr_item_cot_compra);

c02 CURSOR FOR
SELECT	a.nr_item_cot_compra
from	cot_compra_item a
where	a.nr_cot_compra = nr_cot_compra_p
and	coalesce(a.ie_situacao,'A') = 'A'
and not exists (
	SELECT	1
	from	cot_compra_forn_item b
	where	b.nr_cot_compra = nr_cot_compra_p
	and	a.nr_item_cot_compra = b.nr_item_cot_compra
	and	b.vl_unitario_material > 0);


BEGIN
open C01;
loop
fetch C01 into
	nr_item_cot_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	cot_compra_item
	set	ie_situacao = 'I'
	where	nr_item_cot_compra = nr_item_cot_compra_w
	and	nr_cot_compra = nr_cot_compra_p;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_item_cot_compra_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	update	cot_compra_item
	set	ie_situacao = 'I'
	where	nr_item_cot_compra = nr_item_cot_compra_w
	and	nr_cot_compra = nr_cot_compra_p;
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
