			nm_usuario,
			nr_chave,
			nr_seq_impressora,
			nr_seq_relatorio,
			ds_endereco_rede,
			ds_endereco_ip,
			ie_pendente)
	values (		nextval('fila_impressao_etiqueta_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_kit_p,
            		nr_seq_impressora_p,
			nr_seq_relatorio_p,
			ds_endereco_rede_w,
            		ds_endereco_ip_w,
			'S');

