CREATE OR REPLACE PROCEDURE sup_liberar_kit_basico ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;

BEGIN
select	count(*)
into STRICT	qt_existe_w
from	kit_estoque
where	nr_seq_reg_kit = nr_sequencia_p;

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266290);
	--'Não existe kit material vinculados ao kit básico! Verifique. #@#@');
end if;

update 	kit_estoque_reg
set 	nm_usuario_lib = nm_usuario_p,
	dt_liberacao = clock_timestamp()
where 	nr_sequencia = nr_sequencia_p;

commit;
end;
