SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_liberar_parecer_relat_seg ( nr_sequencia_p bigint, cd_pessoa_resp_p text, ie_finalizar_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_item_rel_w		bigint;
nr_seq_parecer_w		bigint;
cd_pessoa_resp_w		varchar(10);
nr_seq_status_w		bigint;
cd_pessoa_usuario_w	varchar(10);


BEGIN 
 
update	segmento_compras_rel_parec 
set	dt_liberacao_parec = clock_timestamp(), 
	nm_usuario_lib_parec = nm_usuario_p, 
	dt_atualizacao = clock_timestamp(), 
	nm_usuario = nm_usuario_p 
where	nr_sequencia = nr_sequencia_p;
 
select	nr_seq_item_rel 
into STRICT	nr_seq_item_rel_w 
from	segmento_compras_rel_parec 
where	nr_sequencia = nr_sequencia_p;
 
select	nr_seq_status_item 
into STRICT	nr_seq_status_w 
from	segmento_compras_rel_it 
where	nr_sequencia = nr_seq_item_rel_w;
 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_usuario_w 
from	usuario 
where	nm_usuario_pesquisa = upper(nm_usuario_p);
 
if (ie_finalizar_p = 'S') then 
	begin 
 
	select	coalesce(max(cd_pessoa_resp),'X') 
	into STRICT	cd_pessoa_resp_w 
	from	status_lista_critica_resp 
	where	nr_seq_lista = nr_seq_status_w 
	and	ie_responsavel_fim = 'S';
 
	/* Caso o usuário que esteja finalizando seja o responsável pelo parecer final, 
	não gera nova necessidade de parecer*/
 
	if (cd_pessoa_resp_w = cd_pessoa_usuario_w) then 
		cd_pessoa_resp_w	:= 'X';
	end if;
 
	end;
else 
	cd_pessoa_resp_w		:= cd_pessoa_resp_p;
end if;
 
if (cd_pessoa_resp_w <> 'X') then 
	begin 
 
	select	nextval('segmento_compras_rel_parec_seq') 
	into STRICT	nr_seq_parecer_w 
	;
 
	insert into segmento_compras_rel_parec( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		cd_pessoa_fisica, 
		nr_seq_item_rel, 
		dt_liberacao) values ( 
			nr_seq_parecer_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_pessoa_resp_w, 
			nr_seq_item_rel_w, 
			clock_timestamp());
 
	CALL sup_envia_ci_resp_parec_lc(nr_seq_parecer_w,nm_usuario_p);
 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
