SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_liberar_registro_kit ( cd_estabelecimento_p bigint, nm_usuario_p text, nr_sequencia_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_nao_consistidos_w		bigint;
ie_permite_lib_sem_cons_w	varchar(1);
ie_consistido_w			varchar(1);


BEGIN

ie_permite_lib_sem_cons_w := substr(obter_valor_param_usuario(143,87,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p),1,1);
ie_consistido_w := substr(obter_se_registro_kit_consist(nr_sequencia_p),1,1);

select	count(*)
into STRICT	qt_nao_consistidos_w
from	kit_estoque_reg a,
	kit_estoque b,
	kit_estoque_comp c
where	a.nr_sequencia = nr_sequencia_p
and	b.nr_seq_reg_kit = a.nr_sequencia
and	c.nr_seq_kit_estoque = b.nr_sequencia
and	c.ie_gerado_barras not in ('S','E');

if (ie_permite_lib_sem_cons_w = 'S') or (qt_nao_consistidos_w = 0) and (ie_consistido_w = 'S') then
	update	kit_estoque_reg
	set	nm_usuario_lib = nm_usuario_p,
		dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
else
	ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(277978);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
