CREATE OR REPLACE PROCEDURE sup_liberar_relat_segmento ( nr_sequencia_p bigint, nr_seq_item_p bigint, ie_forma_liberacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
/* Forma de Liberação 
R : Relatório 
I : Item 
*/
 
 
nr_sequencia_w			bigint;
nr_seq_status_item_w		bigint;
cd_setor_atendimento_w		integer;
cd_pessoa_resp_w			varchar(10);
nr_seq_parecer_w			bigint;
nr_seq_primeiro_parec_w		bigint;
qt_existe_w			bigint;
cd_pessoa_resp_lc_w		varchar(10);

c01 CURSOR FOR 
SELECT	nr_sequencia, 
	nr_seq_status_item, 
	cd_pessoa_resp_lc 
from	segmento_compras_rel_it 
where	nr_seq_seg_rel = nr_sequencia_p 
and	coalesce(dt_liberacao::text, '') = '' 
and	((ie_forma_liberacao_p = 'R') or (ie_forma_liberacao_p = 'I' and nr_sequencia = nr_seq_item_p)) 
order by	1;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_sequencia_w, 
	nr_seq_status_item_w, 
	cd_pessoa_resp_lc_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	update	segmento_compras_rel_it 
	set	dt_liberacao = clock_timestamp(), 
		nm_usuario_lib = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		nm_usuario = nm_usuario_p 
	where	nr_sequencia = nr_sequencia_w;
 
	select	nextval('segmento_compras_rel_parec_seq') 
	into STRICT	nr_seq_parecer_w 
	;
 
	insert into segmento_compras_rel_parec( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		cd_pessoa_fisica, 
		nr_seq_item_rel, 
		dt_liberacao) values ( 
			nr_seq_parecer_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_pessoa_resp_lc_w, 
			nr_sequencia_w, 
			clock_timestamp());
 
	CALL sup_envia_ci_resp_parec_lc(nr_seq_parecer_w,nm_usuario_p);
 
	end;
end loop;
close c01;
 
select	count(*) 
into STRICT	qt_existe_w 
from	segmento_compras_rel_it 
where	nr_seq_seg_rel = nr_sequencia_p 
and	coalesce(dt_liberacao::text, '') = '';
 
if (ie_forma_liberacao_p = 'R') or (qt_existe_w = 0) then 
	begin 
 
	update	segmento_compras_rel 
	set	dt_liberacao = clock_timestamp(), 
		nm_usuario_lib = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		nm_usuario = nm_usuario_p 
	where	nr_sequencia = nr_sequencia_p;
 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
