CREATE OR REPLACE PROCEDURE sup_liberar_solic_kit ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
nr_seq_item_w		integer;
qt_material_w		double precision;
qt_disponivel_w		double precision;
qt_kit_solic_w		double precision;
cd_kit_material_w		integer;
cd_local_estoque_w	smallint;
nr_seq_solic_kit_w		bigint;
cd_estabelecimento_w	smallint;
qt_solicitada_w		bigint;
nr_seq_comp_w		bigint;
nr_sequencia_kit_w		bigint;

c01 CURSOR FOR
SELECT	a.cd_material,
	a.qt_solicitada,
	a.nr_seq_item_kit
from	solic_kit_mat_comp a
where	nr_seq_solic_kit = nr_sequencia_p;

BEGIN

select	qt_solicitada
into STRICT	qt_solicitada_w
from	solic_kit_material
where	nr_sequencia = nr_sequencia_p;

for i in 1..qt_solicitada_w loop
	begin
	select	a.nr_sequencia,
		a.cd_local_estoque,
		a.cd_estabelecimento,
		a.cd_kit_material
	into STRICT	nr_seq_solic_kit_w,
		cd_local_estoque_w,
		cd_estabelecimento_w,
		cd_kit_material_w
	from	solic_kit_material a
	where	nr_sequencia = nr_sequencia_p;

	select	nextval('kit_estoque_seq')
	into STRICT	nr_sequencia_kit_w
	;

	insert into kit_estoque(
		nr_sequencia,
		cd_kit_material,
		dt_atualizacao,
		nm_usuario,
		cd_estabelecimento,
		cd_local_estoque,
		ds_observacao,
		nr_seq_solic_kit)
	values (	nr_sequencia_kit_w,
		cd_kit_material_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_estabelecimento_w,
		cd_local_estoque_w,
		substr(wheb_mensagem_pck.get_texto(307052,'NR_SEQUENCIA_P='||nr_sequencia_p),1,255),
		nr_sequencia_p);

	open c01;
	loop
	fetch c01 into
		cd_material_w,
		qt_material_w,
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	coalesce(max(nr_sequencia),0) +1
		into STRICT	nr_seq_comp_w
		from	kit_estoque_comp
		where	nr_seq_kit_estoque = nr_sequencia_kit_w;

		insert into kit_estoque_comp(
			nr_sequencia,
			nr_seq_kit_estoque,
			cd_material,
			dt_atualizacao,
			nm_usuario,
			qt_material,
			nr_seq_item_kit,
			ie_gerado_barras)
		values (	nr_seq_comp_w,
			nr_sequencia_kit_w,
			cd_material_w,
			clock_timestamp(),
			nm_usuario_p,
			qt_material_w / qt_solicitada_w,
			nr_seq_item_w,
			'N');
		end;
	end loop;
	close c01;
	end;
end loop;

update	solic_kit_material
set	dt_liberacao = clock_timestamp(),
	nm_usuario_lib = nm_usuario_p,
	dt_estorno_lib  = NULL,
	nm_usuario_est_lib  = NULL
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
