CREATE OR REPLACE PROCEDURE sup_liberar_transf_ordem ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_entrega_w	timestamp;
nr_item_oci_w	integer;
qt_material_w	double precision;
			
c01 CURSOR FOR 
SELECT	a.nr_item_oci, 
	a.qt_material 
from	ordem_compra_item a 
where	a.nr_ordem_compra = nr_ordem_compra_p;
	

BEGIN 
delete	FROM ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p;
 
select	max(dt_entrega) 
into STRICT	dt_entrega_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (coalesce(dt_entrega_w::text, '') = '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(198865);
end if;
 
open c01;
loop 
fetch c01 into	 
	nr_item_oci_w, 
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	insert into ordem_compra_item_entrega( 
		nr_sequencia, 
		nr_ordem_compra, 
		nr_item_oci, 
		qt_prevista_entrega, 
		dt_prevista_entrega, 
		dt_atualizacao, 
		nm_usuario) 
	values (nextval('ordem_compra_item_entrega_seq'), 
		nr_ordem_compra_p, 
		nr_item_oci_w, 
		qt_material_w, 
		dt_entrega_w, 
		clock_timestamp(), 
		wheb_mensagem_pck.get_texto(800173));--'Tasy'); 
	end;
end loop;
close c01;
 
update	ordem_compra 
set	dt_liberacao = clock_timestamp(), 
	dt_aprovacao = clock_timestamp() 
where	nr_ordem_compra = nr_ordem_compra_p;
 
update	ordem_compra_item 
set	dt_aprovacao = clock_timestamp() 
where	nr_ordem_compra = nr_ordem_compra_p;
 
CALL gravar_historico_transf_quimio(nm_usuario_p,nr_ordem_compra_p,'L',wheb_mensagem_pck.get_texto(800170));--'TransferÃªncia liberada.'); 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
