SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_obter_email_pf_pj ( cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint, ds_email_p INOUT text) AS $body$
DECLARE

 
ds_retorno_w		varchar(2000);
ds_email_w		varchar(2000);
qt_existe_w		bigint;


BEGIN 
 
if (coalesce(cd_estabelecimento_p::text, '') = '') or (coalesce(cd_pessoa_fisica_p,'X') <> 'X') then 
	begin 
	select	substr(obter_dados_pf_pj(cd_pessoa_fisica_p, cd_cgc_p, 'M'),1,254) 
	into STRICT	ds_retorno_w 
	;
 
	end;
else 
	begin 
	select	ds_email 
	into STRICT	ds_email_w 
	from	pessoa_juridica 
	where	cd_cgc 	= cd_cgc_p;
 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	pessoa_juridica_estab 
	where	cd_cgc 			= cd_cgc_p 
	and	cd_estabelecimento	= cd_estabelecimento_p;
 
	if (qt_existe_w = 0) then 
		ds_retorno_w		:= ds_email_w;
	else 
		begin 
		select	coalesce(ds_email, ds_email_w) 
		into STRICT	ds_retorno_w 
		from	pessoa_juridica_estab 
		where	cd_cgc 			= cd_cgc_p 
		and	cd_estabelecimento	= cd_estabelecimento_p;
	 
		end;
	end if;
	 
	end;
end if;
 
ds_email_p	:= ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
