CREATE OR REPLACE PROCEDURE sup_obter_valor_item_transf ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_unidade_medida_p text, vl_retorno_p INOUT bigint, ds_origem_preco_p INOUT text) AS $body$
DECLARE


cd_material_w			integer;
dt_mesano_vigente_w		timestamp;
vl_custo_medio_w			double precision;
vl_ultima_compra_w		double precision;
vl_retorno_w			double precision;
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_ult_compra_w	varchar(30);
ie_forma_valor_transf_w		varchar(15);
ie_tipo_valor_w			varchar(3);
ie_mes_cm_nf_trans_w		varchar(15);
ie_origem_preco_w			varchar(15);
ds_origem_preco_w			varchar(255);


BEGIN
select	a.cd_material,
	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_p,'UME'),1,30) cd_unidade_medida_estoque,
	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo,
	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_p,'UMC'),1,30) cd_unidade_medida_compra
into STRICT	cd_material_w,
	cd_unidade_medida_estoque_w,
	cd_unidade_medida_consumo_w,
	cd_unidade_medida_compra_w
from	material a
where	a.cd_material = cd_material_p;

select 	coalesce(max(ie_forma_valor_transf),'UC'),
	coalesce(max(ie_mes_cm_nf_trans),'V'),
	coalesce(max(dt_mesano_vigente),trunc(clock_timestamp(),'month'))
into STRICT	ie_forma_valor_transf_w,
	ie_mes_cm_nf_trans_w,
	dt_mesano_vigente_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_mes_cm_nf_trans_w = 'A') then
	select	trunc(clock_timestamp(),'month')
	into STRICT	dt_mesano_vigente_w
	;
end if;

if (cd_unidade_medida_p = cd_unidade_medida_estoque_w) then
	ie_tipo_valor_w := 'UME';
elsif (cd_unidade_medida_p = cd_unidade_medida_compra_w) then
	ie_tipo_valor_w := 'UMP';
elsif (cd_unidade_medida_p = cd_unidade_medida_consumo_w) then
	ie_tipo_valor_w := 'UMC';
else
	ie_tipo_valor_w := 'UME';
end if;

if (ie_forma_valor_transf_w = 'UC') then
	begin
	vl_ultima_compra_w 	:= coalesce((obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_w, 'VU'))::numeric ,0);
	cd_unidade_medida_ult_compra_w 	:= substr(obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_w, 'UC'),1,30);
	vl_ultima_compra_w 	:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_ultima_compra_w, cd_unidade_medida_ult_compra_w, ie_tipo_valor_w),0);
	vl_retorno_w 		:= vl_ultima_compra_w;
	ie_origem_preco_w		:= 'UC';
	end;
elsif (ie_forma_valor_transf_w = 'CM') then
	begin
	vl_custo_medio_w 	:= coalesce(obter_custo_medio_material(cd_estabelecimento_p, dt_mesano_vigente_w, cd_material_w),0);
	vl_custo_medio_w 	:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_custo_medio_w, cd_unidade_medida_estoque_w, ie_tipo_valor_w),0);
	vl_retorno_w 	:= vl_custo_medio_w;
	ie_origem_preco_w	:= 'CM';
	end;
elsif (ie_forma_valor_transf_w = 'CT') then
	begin
	vl_custo_medio_w 	:= coalesce(obter_custo_medio_material(cd_estabelecimento_p, dt_mesano_vigente_w, cd_material_w),0);
	vl_custo_medio_w 	:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_custo_medio_w, cd_unidade_medida_estoque_w, ie_tipo_valor_w),0);
	vl_retorno_w 	:= vl_custo_medio_w;
	ie_origem_preco_w	:= 'CM';

	if (vl_retorno_w = 0) then
		begin
		vl_ultima_compra_w 	:= coalesce((obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_w, 'VU'))::numeric ,0);
		cd_unidade_medida_ult_compra_w 	:= substr(obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_w, 'UC'),1,30);
		vl_ultima_compra_w 	:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_ultima_compra_w, cd_unidade_medida_ult_compra_w, ie_tipo_valor_w),0);
		vl_retorno_w 		:= vl_ultima_compra_w;
		ie_origem_preco_w		:= 'UC';
		end;
	end if;
	end;

elsif (ie_forma_valor_transf_w = 'CTE') then
	begin
	vl_custo_medio_w 	:= coalesce(obter_custo_medio_material(cd_estabelecimento_p, dt_mesano_vigente_w, cd_material_w),0);
	vl_custo_medio_w 	:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_custo_medio_w, cd_unidade_medida_estoque_w, ie_tipo_valor_w),0);
	vl_retorno_w 	:= vl_custo_medio_w;
	ie_origem_preco_w	:= 'CM';

	if (vl_retorno_w = 0) then
		begin
		vl_ultima_compra_w 		:= coalesce((obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_w, 'VU'))::numeric ,0);
		cd_unidade_medida_ult_compra_w 	:= substr(obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_w, 'UC'),1,30);
		vl_ultima_compra_w 		:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_ultima_compra_w, cd_unidade_medida_ult_compra_w, ie_tipo_valor_w),0);
		vl_retorno_w 			:= vl_ultima_compra_w;
		ie_origem_preco_w			:= 'UC';
		end;
	end if;

	if (vl_retorno_w = 0) then
		begin
		vl_ultima_compra_w 		:= coalesce((obter_dados_ult_com_sem_estab(cd_material_w, 'VU'))::numeric ,0);
		cd_unidade_medida_ult_compra_w 	:= substr(obter_dados_ult_com_sem_estab(cd_material_w, 'UC'),1,30);
		vl_ultima_compra_w 		:= coalesce(sup_obter_valor_convertido(cd_material_w, vl_ultima_compra_w, cd_unidade_medida_ult_compra_w, ie_tipo_valor_w),0);
		vl_retorno_w 			:= vl_ultima_compra_w;
		ie_origem_preco_w			:= 'UCSE';
		end;
	end if;
	end;
end if;

ds_origem_preco_w	:= substr(	'#Estab=' || cd_estabelecimento_p ||
			'#Orig=' || ie_origem_preco_w||
			'#ParamOrig=' || ie_forma_valor_transf_w || '/' || ie_mes_cm_nf_trans_w ||
			'#MÃªs=' || to_char(dt_mesano_vigente_w,'mon/yyyy') ||
			'#UM=' || ie_tipo_valor_w,1,255);

vl_retorno_p		:=	vl_retorno_w;
ds_origem_preco_p		:=	ds_origem_preco_w;

end;
$body$
