CREATE OR REPLACE PROCEDURE sup_ordenacao_itens_inventario ( ds_comando_p text, ds_ordem_p text, nr_seq_inventario_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w			bigint;
nr_seq_inventario_w		bigint;
ie_contador_w			bigint := 1;

cursorInventario			integer;
retorno_w				integer;
ds_sql_w				varchar(2000);

/* NÃ£o Documentodo */

BEGIN

begin
ds_sql_w := 	SUBSTR(ds_comando_p, 1, position('ORDER' in upper(ds_comando_p))-1);
ds_sql_w :=	ds_sql_w || ' order by ' || ds_ordem_p;

cursorInventario := DBMS_SQL.OPEN_CURSOR;
DBMS_SQL.PARSE(cursorInventario, ds_sql_w, dbms_sql.native);
DBMS_SQL.DEFINE_COLUMN(cursorInventario, 1, nr_seq_item_w);
DBMS_SQL.DEFINE_COLUMN(cursorInventario, 2, nr_seq_inventario_w);

DBMS_SQL.BIND_VARIABLE(cursorInventario, 'NR_SEQ_INVENTARIO', nr_seq_inventario_p, 32764);

retorno_w := DBMS_SQL.execute(cursorInventario);

while(DBMS_SQL.FETCH_ROWS(cursorInventario) > 0) loop
	DBMS_SQL.COLUMN_VALUE(cursorInventario, 1, nr_seq_item_w);
	DBMS_SQL.COLUMN_VALUE(cursorInventario, 2, nr_seq_inventario_w);

	update	inventario_material
	set	nr_seq_ordem = ie_contador_w
	where	nr_seq_inventario = nr_seq_inventario_w
	and	nr_sequencia = nr_seq_item_w;
	commit;

	ie_contador_w := ie_contador_w + 1;
end loop;
DBMS_SQL.CLOSE_CURSOR(cursorInventario);

exception
when others then
	DBMS_SQL.CLOSE_CURSOR(cursorInventario);
end;

commit;

end;
