SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_palm_grava_config_imp ( nr_seq_impressora_p text, nr_seq_classif_p text, ie_tipo_p text, nm_usuario_p text, cd_setor_atual_p bigint) AS $body$
DECLARE


qt_registro_w		smallint;
nr_sequencia_w		bigint;

BEGIN

select 	count(1)
into STRICT	qt_registro_w
from 	ap_lote_palm_impressora
where	nm_usuario = nm_usuario_p;

if (qt_registro_w = 1) then

	update 	ap_lote_palm_impressora
	set	nr_seq_impressora	= nr_seq_impressora_p,
		nr_seq_classif		= substr(nr_seq_classif_p,0,255),
		ie_tipo 		= ie_tipo_p,
		cd_setor_atual		= cd_setor_atual_p,
		dt_atualizacao 		= clock_timestamp()
	where	nm_usuario 	= nm_usuario_p;
else

	select	coalesce(max(nr_sequencia), 0) + 1
	into STRICT	nr_sequencia_w
	from	ap_lote_palm_impressora;

	insert into ap_lote_palm_impressora(
		nr_sequencia,
		nm_usuario,
		nr_seq_impressora,
		nr_seq_classif,
		ie_tipo,
		dt_atualizacao,
		cd_setor_atual)
	values (nr_sequencia_w,
		nm_usuario_p,
		nr_seq_impressora_p,
		substr(nr_seq_classif_p,0,255),
		ie_tipo_p,
		clock_timestamp(),
		cd_setor_atual_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
