CREATE OR REPLACE PROCEDURE sup_pda_gerar_receb_disp_req ( nr_requisicao_p bigint, ie_opcao_p text, nm_usuario_disp_p text, nm_usuario_receb_p text) AS $body$
DECLARE

/*
ie_opcao_p
R - Recebimento
D - Dispensação
A - Ambos
*/
dt_processo_w	timestamp := clock_timestamp();


BEGIN

if (ie_opcao_p = 'D') then
	update	item_requisicao_material
	set	dt_dispensacao 	= dt_processo_w,
		nm_usuario_disp 	= nm_usuario_disp_p
	where	nr_requisicao 	= nr_requisicao_p
	and	(dt_atendimento IS NOT NULL AND dt_atendimento::text <> '')
	and	coalesce(dt_dispensacao::text, '') = '';
elsif (ie_opcao_p = 'R') then

	update	item_requisicao_material
	set	dt_recebimento 	= dt_processo_w,
		nm_usuario_receb 	= nm_usuario_receb_p
	where	nr_requisicao 	= nr_requisicao_p
	and	(dt_atendimento IS NOT NULL AND dt_atendimento::text <> '')
	and	(dt_dispensacao IS NOT NULL AND dt_dispensacao::text <> '')
	and	coalesce(dt_recebimento::text, '') = '';

	update  requisicao_material
	set	dt_recebimento = dt_processo_w,
		nm_usuario_recebedor = nm_usuario_receb_p
	where	nr_requisicao 	= nr_requisicao_p;


elsif (ie_opcao_p = 'A') then
	update	item_requisicao_material
	set	dt_dispensacao 	= dt_processo_w,
		nm_usuario_disp 	= nm_usuario_disp_p,
		dt_recebimento 	= dt_processo_w,
		nm_usuario_receb 	= nm_usuario_receb_p
	where	nr_requisicao 	= nr_requisicao_p
	and	(dt_atendimento IS NOT NULL AND dt_atendimento::text <> '')
	and	coalesce(dt_dispensacao::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
