CREATE OR REPLACE PROCEDURE sup_receb_solic_unitarizacao ( nr_sequencia_p bigint, cd_pessoa_recebimento_p text, dt_recebimento_p timestamp, cd_local_estoque_p bigint, nr_seq_motivo_unit_p bigint, nm_usuario_p text) AS $body$
DECLARE


--SOLIC_UNITARIZACAO
cd_local_origem_w	solic_unitarizacao.cd_local_estoque_origem%type;
dt_recebimento_w	timestamp;
qt_registros_w		bigint;

cd_operacao_estoque_w	smallint;


BEGIN

dt_recebimento_w := dt_recebimento_p;
if (coalesce(dt_recebimento_w::text, '') = '') then
	dt_recebimento_w := clock_timestamp();
end if;

select	cd_local_estoque_origem
into STRICT	cd_local_origem_w
from	solic_unitarizacao
where	nr_sequencia = nr_sequencia_p;

select	count(*)
into STRICT	qt_registros_w
from	solic_unitarizacao
where	nr_sequencia = nr_sequencia_p
and	coalesce(dt_recebimento::text, '') = '';

if (coalesce(qt_registros_w, 0) > 0) then
	update	solic_unitarizacao
	set	cd_pessoa_recebimento 	= cd_pessoa_recebimento_p,
		dt_recebimento 		= dt_recebimento_w,
		cd_local_estoque_destino = cd_local_estoque_p,
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p
	where	nr_sequencia 		= nr_sequencia_p;

	if (nr_seq_motivo_unit_p <> 0) then
		update	solic_unitarizacao
		set	nr_seq_motivo_unit	= nr_seq_motivo_unit_p
		where	nr_sequencia 		= nr_sequencia_p;
	end if;

	cd_operacao_estoque_w := sup_obter_operacao_unit('ET');

	if (cd_local_estoque_p <> cd_local_origem_w) then
		CALL gerar_movto_estoque_trans_unit(nr_sequencia_p, cd_operacao_estoque_w, '1', 'D', nm_usuario_p);
	else
		--Não é permitido receber a solicitação de unitarização no local de estoque solicitante!
		CALL wheb_mensagem_pck.exibir_mensagem_abort(289222);
	end if;

	commit;
else
	-- Solicitação de unitarização já recebida!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(957403);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
