CREATE OR REPLACE PROCEDURE sup_reenviar_pedido_gimba ( nr_solic_compra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
nr_ordem_compra_w	bigint;


BEGIN

begin
select	coalesce(max(a.nr_ordem_compra),0)
into STRICT	nr_ordem_compra_w
from	ordem_compra_item a
where	a.nr_solic_compra = nr_solic_compra_p;
exception when others then
	nr_ordem_compra_w := 0;
end;

if (nr_ordem_compra_w <> 0) then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	cliente_integracao
	where	nr_seq_inf_integracao = 213
	and	ie_situacao = 'A';

	if (qt_existe_w > 0) then
		CALL gravar_agend_integracao(133,'nr_ordem_compra=' || nr_ordem_compra_w || ';');
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
