CREATE OR REPLACE PROCEDURE sup_registrar_envio_lote_unit ( nr_sequencia_p bigint, cd_pessoa_envio_p text, dt_envio_p timestamp, cd_pessoa_transporte_p text, dt_transporte_p timestamp, cd_local_estoque_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_fornec_w	bigint;
qt_material_w		double precision;
ie_estoque_lote_w		varchar(1);
qt_estoque_lote_w		double precision;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
ds_erro_w		varchar(4000);
cd_operacao_estoque_w	smallint;
ie_permite_unitarizar_w	varchar(1);


BEGIN
select	nr_seq_lote_fornec,
	qt_material
into STRICT	nr_seq_lote_fornec_w,
	qt_material_w
from	solic_unitarizacao
where	nr_sequencia = nr_sequencia_p;


select	coalesce(max(c.ie_estoque_lote),'N')
into STRICT	ie_estoque_lote_w
from	material_lote_fornec a,
	material b,
	material_estab c
where	a.cd_material = b.cd_material
and	c.cd_material = b.cd_material_estoque
and	c.cd_estabelecimento = cd_estabelecimento_w
and	a.nr_sequencia = nr_seq_lote_fornec_w;

ie_permite_unitarizar_w := substr(sup_obter_permite_unitarizar(cd_estabelecimento_w,nr_seq_lote_fornec_w,nm_usuario_p),1,1);

if (ie_permite_unitarizar_w = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(190860);
elsif (ie_estoque_lote_w = 'S') then
	begin
	qt_estoque_lote_w := obter_saldo_lote_fornec(nr_seq_lote_fornec_w);

	if (qt_estoque_lote_w < qt_material_w) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(189897);
	end if;
	end;
end if;

update	solic_unitarizacao
set	cd_pessoa_envio	= cd_pessoa_envio_p,
	dt_envio 	= dt_envio_p,
	cd_pessoa_transporte = cd_pessoa_transporte_p,
	dt_transporte	= dt_transporte_p,
	cd_local_estoque_origem = cd_local_estoque_p,
	dt_atualizacao 	= clock_timestamp(),
	nm_usuario 	= nm_usuario_p
where	nr_sequencia 	= nr_sequencia_p;

cd_operacao_estoque_w := sup_obter_operacao_unit('ST');
CALL gerar_movto_estoque_trans_unit(nr_sequencia_p, cd_operacao_estoque_w, '1', 'O', nm_usuario_p);

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
