CREATE OR REPLACE PROCEDURE sup_registra_envio_oc ( nr_ordem_compra_p bigint, ie_forma_envio_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_cot_compra_w		bigint;
nr_solic_compra_w	bigint;
qt_registros_w		bigint;

C01 CURSOR FOR 
SELECT	distinct 
	nr_cot_compra 
from	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_compra_p;

c02 CURSOR FOR 
SELECT	distinct nr_solic_compra 
from ( 
	SELECT	nr_solic_compra 
	from	cot_compra_item 
	where	nr_cot_compra = nr_cot_compra_w 
	and	nr_solic_compra > 0 
	
union
 
	select	nr_solic_compra 
	from	cot_compra_solic_agrup 
	where	nr_cot_compra = nr_cot_compra_w 
	and	nr_solic_compra > 0) alias0;


BEGIN 
 
select	nextval('ordem_compra_envio_seq') 
into STRICT	nr_sequencia_w
;
 
insert into ordem_compra_envio( 
	nr_sequencia, 
	nr_ordem_compra, 
	dt_atualizacao, 
	nm_usuario, 
	dt_envio, 
	ie_forma_envio, 
	ds_observacao, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec) values ( 
		nr_sequencia_w, 
		nr_ordem_compra_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		ie_forma_envio_p, 
		ds_observacao_p, 
		clock_timestamp(), 
		nm_usuario_p);
		 
open C01;
loop 
fetch C01 into	 
	nr_cot_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	open C02;
	loop 
	fetch C02 into	 
		nr_solic_compra_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
 
		select	count(*) 
		into STRICT	qt_registros_w 
		from	solic_compra 
		where	nr_solic_compra = nr_solic_compra_w;
		 
		if (qt_registros_w > 0) then 
		 
			CALL gerar_hist_solic_sem_commit(	 
				nr_solic_compra_w, 
				WHEB_MENSAGEM_PCK.get_texto(297996), 
				WHEB_MENSAGEM_PCK.get_texto(298004,'NR_ORDEM_COMPRA_P='||nr_ordem_compra_p), 
				'EOC', 
				nm_usuario_p);
		end if;
		end;
	end loop;
	close C02;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
