SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_replica_desc_financ_nf ( nr_seq_nf_p bigint, pr_desconto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_nf_w			bigint;
qt_nf_calculada_w		bigint;
vl_total_item_nf_w		double precision;
vl_desc_financ_w		double precision;

c01 CURSOR FOR
SELECT	nr_item_nf,
	vl_total_item_nf
from	nota_fiscal_item
where	nr_sequencia = nr_seq_nf_p
and	coalesce(vl_total_item_nf,0) > 0
order by	nr_item_nf;


BEGIN

select	count(*)
into STRICT	qt_nf_calculada_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nf_p
and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '');

if (pr_desconto_p = 0) and (qt_nf_calculada_w = 0) then
	begin

	update	nota_fiscal_item
	set	pr_desc_financ = 0,
		vl_desc_financ = 0
	where	nr_sequencia = nr_seq_nf_p;

	end;
elsif (pr_desconto_p <> 0) and (qt_nf_calculada_w = 0) then
	begin

	open c01;
	loop
	fetch c01 into
		nr_item_nf_w,
		vl_total_item_nf_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		vl_desc_financ_w	:= dividir((pr_desconto_p * vl_total_item_nf_w),100);

		update	nota_fiscal_item
		set	pr_desc_financ = pr_desconto_p,
			vl_desc_financ = vl_desc_financ_w
		where	nr_sequencia = nr_seq_nf_p
		and	nr_item_nf = nr_item_nf_w;

		end;
	end loop;
	close c01;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
